        <div class="forecast">
            <WeatherCard temp={temp} condition="Clear" time={time}/>
            <WeatherCard temp={temp2} condition="Clouds" time={"7PM"}/>
            <WeatherCard temp={temp3} condition="Rain" time={"10PM"}/>
        </div>
    )
}

export default WeatherForecast
