import React, {useState} from 'react'
import './WeatherForecast.css'

import WeatherCard from './WeatherCard.js'

function WeatherForecast() {
    const[city, setCity] = useState('Bend, US')
    const[temp, setTemp] = useState('0');

    const data = async () => {
    const response = await fetch(
        `http://api.openweathermap.org/data/2.5/forecast?q=${city},us&units=imperial&mode=json&appid=c640c6cc83953b311bd57ebd01bc9e85`
    );
    const responsejson = await response.json();
    return responsejson;
}
data().then(responsejson=>console.log(responsejson.list[0].main.temp))
    return (
        <div class="forecast">
            <WeatherCard temp={temp} condition="Clear" time={"4PM"}/>
            <WeatherCard temp={23} condition="Clouds" time={"7PM"}/>
            <WeatherCard temp={27} condition="Rain" time={"10PM"}/>
        </div>
    )
}

export default WeatherForecast
