import React from 'react';
import styled from '@emotion/styled';

export default function EquipmentCard(props) {
  const red = 200;
  const Card = styled.div`
  background: linear-gradient(to bottom, rgba(${red},200,200), lightblue);
  width: 300px;
  height: 400px;
  display: inline-block;
  margin: 10px;
  border-radius: 15px;
  color: white;
  line-height: 10px;
  img {
    width: 50%
  }
  `
  const { name } = props;
  const { image } = props;
  return (
    <Card>
    <h2>{ name }</h2>
    <img src={ image }></img>
    </Card>
  );
}
