import React, {useState} from 'react'
import './WeatherForecast.css'

import WeatherCard from './WeatherCard.js'

function WeatherForecast() {
    const[city, setCity] = useState('Bend')
    const[weather, setWeather] = useState({
        time: null,
        condition: null,
        temp: null
    });

    const data = async () => {
    const response = await fetch(
    `http://api.weatherapi.com/v1/forecast.json?key=5a38920fc0804a6c9f205238203010&q=${city}&days=3`
    );
    const responsejson = await response.json();
    console.log("far")
    return responsejson;
}
data().then(responsejson=>{
    console.log(responsejson.main)
    setWeather({
        time: responsejson.main[0].temp,
        condition: responsejson.main[0].temp,
        temp: responsejson.main[0].temp,
    })
    });
    return (
        <div class="forecast">
            <WeatherCard temp={weather.temp} condition="Clear" time={weather.time}/>
            <WeatherCard temp={weather.temp} condition="Clouds" time={"7PM"}/>
            <WeatherCard temp={weather.temp} condition="Rain" time={"10PM"}/>
        </div>
    )
}

export default WeatherForecast
