import React, {useState} from 'react'
import './WeatherForecast.css'
import weather from './Api.js'


import WeatherCard from './WeatherCard.js'

class WeatherForecast extends React.Component {
constructor(props) {
    super(props);
    this.searchCity("Madras")
    this.state = {
      temp: [],
      city: null,
      isLoaded: false
    };
  }

  componentDidMount() {
    this.setState({ isLoaded: true });
  }

  searchCity = async city => {
    await weather
      .get(`daily?city=${city}&key=3f934c9db1cf4675b88fcf1eac2d738d`)
      .then(res => {
        const temp = res.data.data;
        const city = res.data.city_name;

        this.setState({
          temp,
          city
        });
      });
  };

  render() {
    // Methods

    const minTemp = this.state.temp.map(el => {
      return parseInt(el.temp);
    });

    const maxTemp = this.state.temp.map(el => {
      return parseInt(el.max_temp);
    });

    const icon = this.state.temp.map(el => {
      return el.weather.code;
    });

    const description = this.state.temp.map(el => {
      return el.weather.description;
    });
    return (
        <div class="forecast">
            <WeatherCard temp={minTemp[0]} condition="Clear" time={"6PM"}/>
            <WeatherCard temp={minTemp[0]} condition="Clouds" time={"7PM"}/>
            <WeatherCard temp={5} condition="Rain" time={"10PM"}/>
            <WeatherCard temp={5} condition="Rain" time={"10PM"}/>
            <WeatherCard temp={5} condition="Rain" time={"10PM"}/>
            <WeatherCard temp={5} condition="Rain" time={"10PM"}/>
        </div>
    )
}}

export default WeatherForecast
