import React, {Component} from 'react'
import Gauge from './Gauge.js'

class GaugeHolder extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false,
            id: 7001635
        }
    }
    componentDidMount() {

        fetch('https://www.hikingproject.com/data/get-trails-by-id?ids=7001635&key=200962200-a3f1d16081e9f29cfec2bd00a7bd1948')
            .then(res => res.json())
            .then(json => {
                this.setState({
                    isLoaded: true,
                    items: json.trails,
                })
            }).catch((err) => {
                console.log(err);
            });
    }

    render() {

        var { isLoaded, items } = this.state;

        if(!isLoaded) {
            return <div>Loading...</div>;
        }
        else {
        return (
            <div className="Gaugeholder">
                <ul>
                    {items.map(item => (
                        <li key={item.id}>
                            Name: {item.name} | Email: {item.email}
                        </li>
                    ))}
                </ul>
                {items.map(item => (
                <div>
                <p>{item.name}</p>
                <Gauge value={"Length"} number={item.length} id={"gauge-chart3"} percent={item.length*0.03}/>
                </div>
                ))}
            <Gauge value={"Difficulty"} id={"gauge-chart1"} percent={0.3}/>
            <Gauge value={"Altitude"} id={"gauge-chart2"} percent={0.50}/>
            </div>
        )

    }
    }
}
export default GaugeHolder
