import React from 'react';
import styled from '@emotion/styled';

const Value = (props) => {
    const Value = styled.h1`
    font-family: 'Fira Sans', sans-serif;
    font-size: 2rem;
    font-weight: 200;
    `
    var value = props.value
    var number = props.number
    var unit = props.unit
    return (
        <>
            <Value>{value} {number} </Value>
        </>
    )
}

export default Value;
